export interface ApplicationOptions {
  /**
   * The NestJS application author.
   */
  author?: string;
  /**
   * The NestJS application description.
   */
  description?: string;
  /**
   * The NestJS application name.
   */
  name: string;
  /**
   * The NestJS main language configuration
   */
  language?: string;
  /**
   * The NestJS application version.
   */
  version?: string;
}
